const add = function (a, b) {
  return a + b;
};

const subtract = function (a, b) {
  return a - b;
};

const sum = function (array) {
  let total = 0;
  for (number of array) {
    total += number;
  }
  return total;
};

const multiply = function (array) {
  let total = 1;
  for (number of array) {
    total *= number;
  }
  return total;
};

const power = function (a, b) {
  return a ** b;
};

const factorial = function (input) {
  let total = 1;
  for (let i = 1; i <= input; i++) {
    total *= i;
  }
  return total;
};

// Do not edit below this line
module.exports = {
  add,
  subtract,
  sum,
  multiply,
  power,
  factorial,
};
